import React, { Component } from "react";
import API from "../utils/API";
import Header from "../components/Header";
import SearchBar from "../components/SearchBar";
import ResultsContainer from "../components/ResultsContainer";

class Search extends Component {
  state = {
    search: "",
    results: [],
  };

  componentDidMount() {
    this.setState({ results: [], search: "" });
  }

  showResults = (data) => {
    this.setState({ results: data.data.items });
  };

  searchBook = (event) => {
    event.preventDefault();

    API.search(this.state.title)
      .then((results) => this.showResults(results))
      .catch((err) => console.log(err));
  };

  handleInputChange = (event) => {
    const { name, value } = event.target;
    this.setState({
      [name]: value,
    });
  };

  handleSearchSubmit(event) {
    event.preventDefault();
    this.searchBooks(this.state.search);
  }

  render() {
    return (
      <>
        <Header />
        <SearchBar
          value={this.state.search}
          handleInputChange={this.state.handleInputChange}
          handleSearchSubmit={this.state.handleSearchSubmit}
        />
        <ResultsContainer data={this.state.results} />
      </>
    );
  }
}

export default Search;
