  state = {
    search: "",
    result: [],
  };

  searchBook = (event) => {
    event.preventDefault();

    API.search(query)
      .then((res) => setResult(res.data.items))
      .catch((err) => console.log(err));
  };

  handleInputChange = (event) => {
